# https://leetcode.com/problems/longest-palindromic-substring/
class Solution:
    def longestPalindrome(self, s: str) -> str:
        # Function to expand around center and find the longest palindromic substring centered at a given index
        def expand(s, left, right):
            while right< len(s) and left>=0 and s[left]==s[right]:
                left-=1
                right+=1
            return s[left+1:right]
        # Check if the string is empty
        if len(s) < 1:
            return ""
        longest = ""
        for i in range(len(s)):
            # Check for palindromes between at a character
            p1 = expand(s, i, i)
            # Check for palindromes between two characters
            p2 = expand(s, i, i+1)
            if len(p1)>len(longest):
                longest = p1
            if len(p2)>len(longest):
                longest = p2
               
        return longest

if __name__ == "__main__":
    s = ""
    sol = Solution()
    ans = sol.longestPalindrome(s)
    assert ans == ""
    print(ans)